package com.paroquia_santo_afonso.sep.SEP.api.dto.builder;import java.io.IOException;import com.paroquia_santo_afonso.sep.SEP.api.dto.ListarEquipeDTO;import com.paroquia_santo_afonso.sep.SEP.api.dto.SalvarEquipeDTO;import com.paroquia_santo_afonso.sep.SEP.domain.model.Encontro;import com.paroquia_santo_afonso.sep.SEP.domain.model.Equipe;import com.paroquia_santo_afonso.sep.SEP.domain.model.Pasta;public class EquipeBuilder {	public static ListarEquipeDTO toListarEquipeDTO(Equipe equipe) {		return ListarEquipeDTO.builder()				.id(equipe.getId())				.equipe(equipe.getNome())				.pasta(equipe.getPasta())				.encontro(ListarEncontroEquipeBuilder.toListarEncontroEquipe(equipe.getEncontro()))				.build();	}	public static ListarEquipeDTO toListarEquipeDTOParaEncontro(Equipe equipe) {		return ListarEquipeDTO.builder()				.id(equipe.getId())				.equipe(equipe.getNome())				.build();	}	public static Equipe toEquipe(SalvarEquipeDTO salvarEquipeDTO) throws IOException {		return Equipe.builder()				.nome(salvarEquipeDTO.getEquipe())				.encontro(new Encontro(salvarEquipeDTO.getEncontroId()))				.build();	}}